import React, { Component, TableRow } from 'react';
//import { addListener } from '../../server/src/app';
import './App.css';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      url: 'this is a placeholder',
      urls: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.fetchUrls = this.fetchUrls.bind(this);
  }  


  getData(){

  }
  componentDidMount(){
    this.fetchUrls();
  }

  handleChange(event) {
    this.setState({url: event.target.value});
  }

  async handleSubmit(event, url) {
    event.preventDefault();

   try{
    await fetch('/urls', {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({url: this.state.url}),
    });
    }catch(err){
     console.error()
    }
 }

 fetchUrls(event){
  console.log('urlsss', this.state.urls)
  fetch('/urls')
    .then(res => res.json())
    .then(urls => this.setState({ urls }));
 }



  render() {

    var heading = ['Name', 'City', 'Course'];
    var body =
        [['Kapil', 'Jaipur', 'MCA'],
        ['Aakash', 'Hisar', 'Btech'],
        ['Mani', 'Ranchi', 'MSc'],
        ['Yash', 'Udaipur', 'Mtech']
        ];

    return(

      <div className="App">
        <h1>Short URL</h1>

        <form onSubmit={this.handleSubmit}>
          <label>
            URL Adress:
            <input type="text" value={this.state.value} onChange={this.handleChange} />
          </label>
          <input type="submit" value="Submit" />
        </form> 


        <button onClick={this.fetchUrls}>
          fetch urls
        </button>
   <br></br>


            <div >
                <Table heading={heading} body={body} />,
            </div>

        {this.state.urls.map(url =>
          <div key={url._id}>{url.shortUrl}</div>
        )}

      </div>
    )
  }
}

export default App;
